import React, { useState } from 'react'
import { ACCOUNT_TYPE } from '../../../utils/constants';
import { Tab } from '../../common/Tab';
import {AiOutlineEye,AiOutlineEyeInvisible} from 'react-icons/ai'
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import {toast} from 'react-hot-toast'

import { setSignupData } from '../../../slices/authSlice';
export const SignupForm = () => {
    const [accountType,setAccountType]=useState(ACCOUNT_TYPE.STUDENT);
    const dispatch=useDispatch();
    const navigate=useNavigate();
    const tabData=[
        {
            id:1,
            tabName:"Student",
            type:ACCOUNT_TYPE.STUDENT,
        },
        {
            id:2,
            tabName:"Instructor",
            type:ACCOUNT_TYPE.INSTRUCTOR
        }
    ]
    const [showPassword,setShowPassword]=useState(false);
    const [showConfirmPassword,setShowConfirmPassword]=useState(false);

    const [formData,setFormData]=useState({
      firstName:"",
      lastName:"",
      email:"",
      password:"",
      confirmPassword:""
    })
    const {firstName,lastName,email,password,confirmPassword}=formData;

    const handleOnChange=(e)=>{
      setFormData((prev)=>({
        ...prev,
        [e.target.name]:e.target.value
      }))
    }
    const handleOnSubmit=async (e)=>{
      e.preventDefault();
      if(password!=confirmPassword){
          toast.error("Password not matched with confirm password");
          navigate('/signup')
          return;
      }
      const signupData={
        ...formData,accountType
      }
      dispatch(setSignupData(signupData));
    
      dispatch(sendOtp(formData.email, navigate))

      setFormData({
        firstName:"",
        lastName:"",
        email:"",
        password:"",
        confirmPassword:""
      });
      setAccountType("Student");
    }
  return (
    <div>
    <Tab tabData={tabData} field={accountType} setField={setAccountType}/>

    <form onSubmit={handleOnSubmit}>

      <label >First Name
      <input
      type="text"
      name="firstName"
      value={firstName}
      onChange={handleOnChange}
      required 
      className="bg-slate-500 border-[2px]"
      ></input>
      </label>

      <label>Last Name
      <input
      type="text"
      name="lastName"
      onChange={handleOnChange}
      required
      className="bg-slate-500 border-[2px]"
      >
      </input>
      </label>

      <label>Email address
      <input
      type="text"
      name="email"
      onChange={handleOnChange}
      required
      className="bg-slate-500 border-[2px]"
      >
      </input>
      </label>

      <label>password
      <input
      type={showPassword?"text":"password"}
      name="password"
      onChange={handleOnChange}
      required
      className="bg-slate-500 border-[2px]"
      >
      </input>
    <AiOutlineEye onClick={()=>{setShowPassword(true);}}/>
      <AiOutlineEyeInvisible onClick={()=>{
        setShowPassword(false);
      }
      }/>
      </label>

      <label>Confirm Password
      <input
      type={showConfirmPassword?"text":"password"}
      name="confirmPassword"
      onChange={handleOnChange}
      required
      className="bg-slate-500 border-[2px]"
      >
      </input>
    <AiOutlineEye onClick={()=>{setShowConfirmPassword(true)}}/>
      <AiOutlineEyeInvisible onClick={()=>{
        setShowConfirmPassword(false);
      }
      }/>
      </label>
    
    <button type="submit" className="bg-orange-300  ">
        Create Account
    </button>
    </form>

    </div>
  )
}
