import React, { useRef } from 'react'
import { useDropzone } from "react-dropzone"
import { useState } from 'react';
import { Player } from "video-react"
import { useEffect } from 'react';
export const Upload = ({
    name,
    register,
    setValue,
    getValuse,
    editData=null,
    video=null,
    viewData=null
}) => {
   const [selectedFile,setSelectedFile]=useState();
   const [previewSource,setPrviewSource]=useState();

   const onDrop=(acceptedFile)=>{
    const file=acceptedFile[0];
    previewFile(file);
    setSelectedFile(file);
   }
   const {getRootProps,getInputProps,isDragValue}=useDropzone({
    accept:!video?{ "image/*": [".jpeg", ".jpg", ".png"] }
    :{"video/*":[".mp4"]}
   ,onDrop
   })

   const previewFile=(file)=>{
    const reader=new FileReader();
    reader.readAsDataURL(file)
    reader.onloadend=()=>{
        setPrviewSource();
    }
   }
   const inputRef=useRef();

   useEffect(()=>{
    register(name,{required:true})
   },[]);
   useEffect(()=>{
    setValue(name,selectedFile);
   },[selectedFile]);
  return (
   <div>
        {
            previewSource?
            <div>
            {
                !video ?<img src={previewSource}  alt="image"/>
                :
                <Player aspectRatio='16:9' playsInline src={previewSource}/>
            }
            {
                !viewData &&
                <button className="bg-orange-400" onClick={()=>
                                {
                                    setPrviewSource(null);
                                    setSelectedFile(null);
                                    setValue(name,null);
                                }}   >
                cancel
                </button>
            }
            </div>
            :
            (<div {...getRootProps()}>
                <input {...getInputProps()} ref={inputRef} />
                <div> Enter to add image</div>
            </div>)
        }
   </div>
  )
}
