import React from 'react'
import { toast } from 'react-toastify'

export const Tab = ({tabData,field,setField}) => {
  return (
    <div>
        {
            tabData.map((tab)=>{
                <button key={tabData.id}
                onClick={()=>setField(tabData.type)
                }
                className={`${field==tabData.type}?" text-red-600":"text-"`}>
                {tabData.tabName}
                </button>
                {
                  toast.success("changed");
                }
            })
        }
    </div>
  )
}
