    }
  return (
    <div>
    <Tab tabData={tabData} field={accountType} setField={setAccountType}/>

    <form onSubmit={handleOnSubmit}>

      <label >First Name
      <input
      type="text"
      name="firstName"
      value={firstName}
      onChange={handleOnChange}
      required 
      className="bg-slate-500 border-[2px]"
      ></input>
      </label>

      <label>Last Name
      <input
      type="text"
      name="lastName"
      onChange={handleOnChange}
      required
      className="bg-slate-500 border-[2px]"
      >
      </input>
      </label>

      

    </form>

    </div>
  )
}
