import React, { useEffect } from 'react'
import { toast } from 'react-toastify'

export const Tab = ({tabData,field,setField}) => {
  
  return (
    <div>
        {
            tabData.map((tab,index)=>(
                <button key={index}
                onClick={()=>{setField(tabData.type)}
                }
                className={`${field==tab.type}?" text-red-900":" text-yellow-500"`}
                >
                {tab.tabName}
                </button>
                
                
            ))
        }
    </div>
  )
}
