import React from 'react'
import { useForm } from 'react-hook-form'
import { ChipInput } from './ChipInput';

export const CourseInformationForm = () => {
  const {register,setValue,getValues,formState:{errors}}=useForm();

  return (
    <div>
      <label>Course Title
      <input
      id="courseTitle"
      placeholder="Enter the course title."
      {...register("courseTitle",{required:true})}
      classname="bg-slate-700"
      ></input>
      {errors.courseTitle && <div>Enter valid course Title</div>}
      </label>
      <br></br>
      <label>
      Course Short Description
      <input
      id="courseShortDes"
      placeholder="Enter course Short Description"
      {...register("courseShortDesc",{required:true})}
      className="bg-slate-600"
      >
      </input>
      {errors.courseShortDes &&  <div>Enter valid description</div>}
      </label>
      <br></br>

      <label>
        Price
        <input
        id="price"
        placeholder="Enter the price."
        {...register("price",{required:true})}
        ></input>
        {errors.price && <div>Enter valid price</div>}
      </label>
       
       <label>
        Tags
        <ChipInput
          name={"courseTags"}
          register={register}
          setValue={setValue}
          getValues={getValues}
          errors={errors}
        />
       </label>

       <Upload/>
    
    </div>
  )
}
