import React, { useEffect } from 'react'
import { useForm } from 'react-hook-form'
import { useDispatch, useSelector } from 'react-redux';
import {toast} from 'react-hot-toast';
import { addCourseDetails } from '../../../../services/operations/courseAPI';
import {setStep,setCourse} from '../../../../slices/courseSlice'
import {HiOutlineCurrencyRupee} from 'react-icons/hi';
import ChipInput from './ChipInput';
import { Upload } from './Upload';
import { courseEndpoints } from '../../../../services/apis';
const {COURSE_STATUS}=courseEndpoints
export const CourseInformationForm = () => {
    const {register,handleSubmit,setValue,
        formState:{errors},getValues}=useForm();
        const dispatch=useDispatch();
        const {token}=useSelector((state)=>state.auth);
        const {course,editCourse}=useSelector((state)=>state.course);
        useEffect(()=>{
            if(editCourse){
                setValue("courseTitle",course.courseName);
                setValue("courseShorDesc",course.Description);
                setValue("coursePrice",course.price);
                setValue("courseTags",course.tag);
                setValue("courseBenefits",course.whatYouWillLearn);
                setValue("courseRequirements",course.instructions);
                setValue("courseImage",course.thumbnail);
            }
        },[])

        const isFormUpdated = () => {
            const currentValues = getValues()
            // console.log("changes after editing form values:", currentValues)
            if (
              currentValues.courseTitle !== course.courseName ||
              currentValues.courseShortDesc !== course.courseDescription ||
              currentValues.coursePrice !== course.price ||
              currentValues.courseTags.toString() !== course.tag.toString() ||
              currentValues.courseBenefits !== course.whatYouWillLearn ||
              currentValues.courseRequirements.toString() !==
                course.instructions.toString() ||
              currentValues.courseImage !== course.thumbnail
            ) {
              return true
            }
            return false
          }
          const onSubmit=async (data)=>{

            if(editCourse){
               
                }else {
                    toast.error("No changes made to the form")
                  }
            
            const formData=new FormData();
            formData.append("courseName", data.courseTitle)
            formData.append("courseDescription", data.courseShortDesc)
            formData.append("price", data.coursePrice)
            formData.append("tag", JSON.stringify(data.courseTags))
            formData.append("whatYouWillLearn", data.courseBenefits)
            formData.append("instructions", JSON.stringify(data.courseRequirements))
            formData.append("thumbnailImage", data.courseImage)
            formData.append("status", COURSE_STATUS.DRAFT)
            const result = await addCourseDetails(formData)
            if (result) {
                dispatch(setStep(2))
                dispatch(setCourse(result))
              }
          }
  return (
    <form onSubmit={handleSubmit(onSubmit)}>

              {/* Course Title */}
      <div className="flex flex-col space-y-2">
        <label className="text-sm text-richblack-5" htmlFor="courseTitle">
          Course Title <sup className="text-pink-200">*</sup>
        </label>
        <input
          id="courseTitle"
          placeholder="Enter Course Title"
          {...register("courseTitle", { required: true })}
          className="form-style w-full"
        />
        {errors.courseTitle && (
          <span className="ml-2 text-xs tracking-wide text-pink-200">
            Course title is required
          </span>
        )}
      </div>
      {/* Course Short Description */}
      <div className="flex flex-col space-y-2">
        <label className="text-sm text-richblack-5" htmlFor="courseShortDesc">
          Course Short Description <sup className="text-pink-200">*</sup>
        </label>
        <textarea
          id="courseShortDesc"
          placeholder="Enter Description"
          {...register("courseShortDesc", { required: true })}
          className="form-style resize-x-none min-h-[130px] w-full"
        />
        {errors.courseShortDesc && (
          <span className="ml-2 text-xs tracking-wide text-pink-200">
            Course Description is required
          </span>
        )}
      </div>
      {/* Course Price */}
      <div className="flex flex-col space-y-2">
        <label className="text-sm text-richblack-5" htmlFor="coursePrice">
          Course Price <sup className="text-pink-200">*</sup>
        </label>
        <div className="relative">
          <input
            id="coursePrice"
            placeholder="Enter Course Price"
            {...register("coursePrice", {
              required: true,
              valueAsNumber: true,
              pattern: {
                value: /^(0|[1-9]\d*)(\.\d+)?$/,
              },
            })}
            className="form-style w-full !pl-12"
          />
          <HiOutlineCurrencyRupee className="absolute left-3 top-1/2 inline-block -translate-y-1/2 text-2xl text-richblack-400" />
        </div>
        {errors.coursePrice && (
          <span className="ml-2 text-xs tracking-wide text-pink-200">
            Course Price is required
          </span>
        )}
      </div>
      <ChipInput
        label="Tags"
        name="courseTags"
        placeholder="Enter Tags and press Enter"
        register={register}
        errors={errors}
        setValue={setValue}
        getValues={getValues}
      />
      <Upload
        name="courseImage"
        label="Course Thumbnail"
        register={register}
        setValue={setValue}
        errors={errors}
        editData={editCourse ? course?.thumbnail : null}
      />
       <div className="flex flex-col space-y-2">
        <label className="text-sm text-richblack-5" htmlFor="courseBenefits">
          Benefits of the course <sup className="text-pink-200">*</sup>
        </label>
        <textarea
          id="courseBenefits"
          placeholder="Enter benefits of the course"
          {...register("courseBenefits", { required: true })}
          className="form-style resize-x-none min-h-[130px] w-full"
        />
        {errors.courseBenefits && (
          <span className="ml-2 text-xs tracking-wide text-pink-200">
            Benefits of the course is required
          </span>
        )}
      </div>
      <button type='submit'>Submit</button>
    </form>
  )
}
