import React, { useDebugValue } from 'react'
import { useForm } from 'react-hook-form'
import { ChipInput } from './ChipInput';
import { useDispatch, useSelector } from 'react-redux';
import { Upload } from './Upload';
import { setStep ,setCourse} from '../../../../slices/courseSlice';
import { addCourseDetails } from '../../../../services/operations/courseAPI';
export const CourseInformationForm = () => {
  const {register,setValue,getValues,formState:{errors}}=useForm();
  const {editCourse}=useSelector((state)=>state.course);
  const {course}=useSelector((state)=>state.course);
  const {token}=useSelector((state)=>state.auth);
  const dispatch=useDispatch();
  const formUpdated=()=>{
    const values=getValues();
    if(values.courseTitle !== course.courseName
      || values.courseShortDes!==course.courseDescription
      || values.price !== course.price||
      values.courseBenefits !== course.whatYouWillLearn
      ||values.courseImage !== course.thumbnail){
        return true;
    }else{
      return false;
    }
  }
  const handleOnSubmit= async (event,data)=>{
    event.preventDefault();
    if(editCourse){
      if(formUpdated){

      }
    }
    const formData=new FormData();
    formData.append("courseName",courseTitle);
    formData.append("courseDescription",data.courseShortDes);
    formData.append("price",data.price);
    formData.append("whatYouWillLearn",data.courseBenefits);
    formData.append("thumbnailImage", data.courseImage)

    const result = await addCourseDetails(formData, token)
    console.log(result);
    if (result) {
      dispatch(setStep(2))
      dispatch(setCourse(result))
    }
   
  }


  return (
    <form onSubmit={handleOnSubmit}>
      <label>Course Title
      <input
      id="courseTitle"
      placeholder="Enter the course title."
      {...register("courseTitle",{required:true})}
      classname="bg-slate-700"
      ></input>
      {errors.courseTitle && <div>Enter valid course Title</div>}
      </label>
      <br></br>
      <label>
      Course Short Description
      <input
      id="courseShortDes"
      placeholder="Enter course Short Description"
      {...register("courseShortDesc",{required:true})}
      className="bg-slate-600"
      >
      </input>
      {errors.courseShortDes &&  <div>Enter valid description</div>}
      </label>
      <br></br>

      <label>
        Price
        <input
        id="price"
        placeholder="Enter the price."
        {...register("price",{required:true})}
        ></input>
        {errors.price && <div>Enter valid price</div>}
      </label>
       
       <label>
        Tags
        <ChipInput
          name={"courseTags"}
          register={register}
          setValue={setValue}
          getValues={getValues}
          errors={errors}
        />
       </label>

       <Upload
        name="courseThumbnail"
        register={register}
        setValue={setValue}
        getValues={getValues}
        editData={editCourse?course?.thumbnail:null}
       />
       <div className="flex flex-col space-y-2">
        <label className="text-sm text-richblack-5" htmlFor="courseBenefits">
          Benefits of the course <sup className="text-pink-200">*</sup>
        </label>
        <textarea
          id="courseBenefits"
          placeholder="Enter benefits of the course"
          {...register("courseBenefits", { required: true })}
          className="form-style resize-x-none min-h-[130px] w-full"
        />
        {errors.courseBenefits && (
          <span className="ml-2 text-xs tracking-wide text-pink-200">
            Benefits of the course is required
          </span>
        )}
      </div>
      <button type="submit"> Submit</button>

    
    </form>
  )
}
