import { apiConnector } from "../apiConnector";
import { endpoints } from "../apis";
import {toast} from 'react-hot-toast'
const {SENDOTP_API,SIGNUP_API}=endpoints;
export function sendOTP(email, navigate) {
  return async (dispatch) => {
    const toastId = toast.loading("Loading...")
    try {
      const response = await apiConnector("POST", SENDOTP_API, {
        email,
        checkUserPresent: true,
      })
      console.log("SENDOTP API RESPONSE............", response)

      console.log(response.data.success)

      if (!response.data.success) {
        throw new Error(response.data.message)
      }

      toast.success("OTP Sent Successfully")
      navigate("/verify-email")
    } catch (error) {
      console.log("SENDOTP API ERROR............", error)
      toast.error("Could Not Send OTP")
    }
    toast.dismiss(toastId)
  }
}

export function setSignup(firstName,lastName,password,
                        confirmPassword,otp,accountType,email,navigate)
  {
    return async (dispatch)=>{
      const toastId=toast.loading("Loading...");
      try{
        console.log("")
        const res=await apiConnector("POST",SIGNUP_API,{
          firstName,
          lastName,
          password,
          confirmPassword,
          otp,
          accountType,
          email
        });
        if(!res.data.success){
          throw new Error(res.data.message);
        }
        toast.success("Sign up successfully");
        navigate('/login');
    
      }catch(error){
        console.log("Failed to sign up data.");
        console.log(error);
      }
      toast.dismiss(toastId)
    }
  
}
