import React, { useEffect, useState } from 'react'

export const ChipInput = ({name,register,setValue,getValues,errors}) => {
    const [chips,setChips]=useState([]);
    useEffect(()=>{
        {register(name,{required:true})}
    },[])
    useEffect(()=>{
        setChips(name,chips);
    },[chips])

    const handleKeyDown=(e)=>{
        if(e.key=='Enter'){
            e.prev
            const chipValue=[e.target.value];
            if(chipValue && !chips.includes(chipValue)){
                const update=[...chips,chipValue];
                setChips(update);
            }
            setValue(name,"");
        }
        console.log(chips);
    }
  return (
    <div>
    
    <label>
        Tags
        <input
        id="courseTags"
        placeholder="Enter the tag"
        onKeyDown={handleKeyDown}
        >
        </input>
    </label>
    
    </div>
  )
}
