import React from 'react'
import { useForm } from 'react-hook-form'
import { ChipInput } from './ChipInput';
import { useSelector } from 'react-redux';
import { Upload } from './Upload';
export const CourseInformationForm = () => {
  const {register,setValue,getValues,formState:{errors}}=useForm();
  const {editCourse}=useSelector((state)=>state.course);
  const {course}=useSelector((state)=>state.course);
  
  const formUpdated=()=>{
    const values=getValues();
    if(values.courseTitle !== course.courseName
      || values.courseShortDes!==course.courseDescription
      || values.price !== course.price||
      values.courseBenefits !== course.whatYouWillLearn
      ||values.courseImage !== course.thumbnail){
        return true;
    }else{
      return false;
    }
  }
  const handleOnSubmit= async (data)=>{
    if(editCourse){
      if(formUpdated){

      }
    }
    const formData=new FormData();
    formData.append("courseName",data.courseTitle);
    formData.append("courseDescription",data.courseShortDes);
    formData.append("price",data.price);
    formData.append("whatYouWillLearn",data.courseBenefits);
    formData.append("thumbnailImage", data.courseImage)

    const result = await addCourseDetails(formData, token)
    if (result) {
      dispatch(setStep(2))
      dispatch(setCourse(result))
    }
    setLoading(false)
  }


  return (
    <form onSubmit={handleOnSubmit}>
      <label>Course Title
      <input
      id="courseTitle"
      placeholder="Enter the course title."
      {...register("courseTitle",{required:true})}
      classname="bg-slate-700"
      ></input>
      {errors.courseTitle && <div>Enter valid course Title</div>}
      </label>
      <br></br>
      <label>
      Course Short Description
      <input
      id="courseShortDes"
      placeholder="Enter course Short Description"
      {...register("courseShortDesc",{required:true})}
      className="bg-slate-600"
      >
      </input>
      {errors.courseShortDes &&  <div>Enter valid description</div>}
      </label>
      <br></br>

      <label>
        Price
        <input
        id="price"
        placeholder="Enter the price."
        {...register("price",{required:true})}
        ></input>
        {errors.price && <div>Enter valid price</div>}
      </label>
       
       <label>
        Tags
        <ChipInput
          name={"courseTags"}
          register={register}
          setValue={setValue}
          getValues={getValues}
          errors={errors}
        />
       </label>

       <Upload
        name="courseThumbnail"
        register={register}
        setValue={setValue}
        getValues={getValues}
        editData={editCourse?course?.thumbnail:null}
       />
    
    </form>
  )
}
