import { useDispatch } from "react-redux";
import { apiConnector } from "../apiConnector";
import { endpoints } from "../apis";
import {toast} from 'react-hot-toast'
import { setUser } from "../../slices/profileSlice";
import { setToken } from "../../slices/authSlice";
const {SENDOTP_API,SIGNUP_API, LOGIN_API}=endpoints;
export function sendOTP(email, navigate) {
  return async (dispatch) => {
    const toastId = toast.loading("Loading...")
    try {
      const response = await apiConnector("POST", SENDOTP_API, {
        email,
      })
      console.log("SENDOTP API RESPONSE............", response)

      console.log(response.data.success)

      if (!response.data.success) {
        throw new Error(response.data.message)
      }

      toast.success("OTP Sent Successfully")
      navigate("/verify-email")
    } catch (error) {
      console.log("SENDOTP API ERROR............", error)
      toast.error("Could Not Send OTP")
    }
    toast.dismiss(toastId)
  }
}

export function setSignup({firstName,
  lastName,
  password,
  confirmPassword,
  otp,
  accountType,
  email},navigate)
  {
    return async (dispatch)=>{
      const toastId=toast.loading("Loading...");
      try{
        const res=await apiConnector("POST",SIGNUP_API,{
          firstName,
          lastName,
          password,
          confirmPassword,
          otp,
          accountType,
          email
        });
        if(!res.data.success){
          throw new Error(res.data.message);
        }
        toast.success("Sign up successfully");
        navigate('/login');
    
      }catch(error){
        console.log("Failed to sign up data.");
        console.log(error);
      }
      toast.dismiss(toastId)
    }
  
}

export const sendLogin= async ({email,password},navigate,dispatch)=>{
  const toastId=toast.loading("Loading...");
    try{
      const res=await apiConnector("POST", LOGIN_API,{
        email,password
      })
      if(!res.data.success){
        throw new Error(res.data.message);
      }
      localStorage.setItem("token",JSON.stringify(res.data.token));
      localStorage.setItem("user",JSON.stringify(res.data.user));
      dispatch(setUser(res.data.user));
      dispatch(setToken(res.data.token))
      toast.success("Login successfully.");
      navigate("/dashboard/my-profile");

    }catch(error){
      console.log("Failed to send login data");
      console.log(error);
    }
    toast.dismiss(toastId);
}

export const logout=async (dispatch,user,token,navigate)=>{
    dispatch(setToken(null));
    dispatch(setUser(null));
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    toast.success("Loggout Successfully.");
    navigate('/');
}
