import React, { useEffect, useState } from 'react'

export const ChipInput = ({name,register,setValue,getValues,errors}) => {
    const [chips,setChips]=useState([]);
    const handleKeyDown=(e)=>{
        if(e.key=='Enter'){
            e.preventDefault();
            const chipValue=e.target.value;
            if(chipValue && !chips.includes(chipValue)){
                const update=[...chips,chipValue];
                
                setChips(update);
                console.log(chips)
                e.target.value=""
            }
        }
    }
  return (
    <div>
      {
        chips.map((data,index)=>{
            return (
                <div className=' text-yellow-500'>{data}</div>
            );
        })
      }
    <label>
        Tags
        <input
        id="courseTags"
        placeholder="Enter the tag"
        onKeyDown={handleKeyDown}
        >
        </input>
    </label>
    
    </div>
  )
}
