import React from 'react'
import OtpInput from 'react-otp-input';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { setSignup } from '../services/operations/authAPI';
export const VerifyEmail = () => {
    const [otp, setOtp] = useState('');
    const {signupData,loading}=useSelector((state)=>state.auth)
    const navigate=useNavigate();
    const handleSubmit= ()=>{
        const {firstName,lastName,password,email,
            confirmPassword,accountType}=signupData;
        try{
            const response= setSignup(
                firstName,lastName,
                password,email,confirmPassword,accountType,
                otp,navigate
            );
            console.log(response.data);
        }catch(error){
            console.log(error);
            console.log("Problem in verify email page submit button");
        }
    }
  return (
    <div className="bg-slate-600 w-[120px] border-[1px]">
    <OtpInput
    
      value={otp}
      onChange={setOtp}
      numInputs={6}
      renderSeparator={<span>-</span>}
      renderInput={(props) => <input {...props} />}
      
      
    />
    <button onClick={handleSubmit}
    className=" bg-yellow-400 border-[1px]">Verify Email</button>
    
    </div>
  )
}
